CREATE TABLE user_table (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), email VARCHAR(255) UNIQUE NOT NULL, full_name VARCHAR(255), role VARCHAR(50) DEFAULT 'user', specialite VARCHAR(100), nom_affichage VARCHAR(255), entete_ordonnance TEXT, signature_image_url TEXT, message_email_pdf TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE patient (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, prenom VARCHAR(255) NOT NULL, genre VARCHAR(50), date_naissance DATE, telephone VARCHAR(50), email VARCHAR(255), adresse TEXT, numero_securite_sociale VARCHAR(100), medecin_traitant VARCHAR(255), antecedents_medicaux TEXT, allergie TEXT, atcd_ophtalmologiques TEXT, ald BOOLEAN DEFAULT FALSE, cmu BOOLEAN DEFAULT FALSE, suivi_ivt BOOLEAN DEFAULT FALSE, statut_salle_attente VARCHAR(50) DEFAULT 'en_attente', date_arrivee TIMESTAMP, en_dilatation BOOLEAN DEFAULT FALSE, heure_dilatation TIMESTAMP, dossier_ouvert_par VARCHAR(255), dossier_ouvert_date TIMESTAMP, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE examenorthoptiste (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_examen TIMESTAMP NOT NULL, motif_consultation TEXT, allergie TEXT, atcd_med TEXT, atcd_oph TEXT, pio_od VARCHAR(50), pio_og VARCHAR(50), pachymetrie_od VARCHAR(50), pachymetrie_og VARCHAR(50), pio_corrige_od VARCHAR(50), pio_corrige_og VARCHAR(50), keratometrie_od_k1 VARCHAR(50), keratometrie_od_k1_axe VARCHAR(50), keratometrie_od_k2 VARCHAR(50), keratometrie_od_k2_axe VARCHAR(50), keratometrie_od_km VARCHAR(50), keratometrie_od_km_axe VARCHAR(50), keratometrie_og_k1 VARCHAR(50), keratometrie_og_k1_axe VARCHAR(50), keratometrie_og_k2 VARCHAR(50), keratometrie_og_k2_axe VARCHAR(50), keratometrie_og_km VARCHAR(50), keratometrie_og_km_axe VARCHAR(50), lunettes_portees_od_sphere VARCHAR(50), lunettes_portees_od_cylindre VARCHAR(50), lunettes_portees_od_axe VARCHAR(50), lunettes_portees_od_av VARCHAR(50), lunettes_portees_og_sphere VARCHAR(50), lunettes_portees_og_cylindre VARCHAR(50), lunettes_portees_og_axe VARCHAR(50), lunettes_portees_og_av VARCHAR(50), autoref_od_sphere VARCHAR(50), autoref_od_cylindre VARCHAR(50), autoref_od_axe VARCHAR(50), autoref_og_sphere VARCHAR(50), autoref_og_cylindre VARCHAR(50), autoref_og_axe VARCHAR(50), pd VARCHAR(50), ps_od VARCHAR(50), ps_og VARCHAR(50), ref_subjective_sous_skiacol BOOLEAN DEFAULT FALSE, ref_subjective_od_sphere VARCHAR(50), ref_subjective_od_cylindre VARCHAR(50), ref_subjective_od_axe VARCHAR(50), ref_subjective_od_avl VARCHAR(50), ref_subjective_od_add VARCHAR(50), ref_subjective_od_avp VARCHAR(50), ref_subjective_og_sphere VARCHAR(50), ref_subjective_og_cylindre VARCHAR(50), ref_subjective_og_axe VARCHAR(50), ref_subjective_og_avl VARCHAR(50), ref_subjective_og_add VARCHAR(50), ref_subjective_og_avp VARCHAR(50), examen_motilite TEXT, convergence TEXT, phories TEXT, tropies TEXT, notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE examenophtalmologie (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_examen TIMESTAMP NOT NULL, lampe_fente_od TEXT, lampe_fente_og TEXT, fond_oeil_od TEXT, fond_oeil_og TEXT, diagnostic TEXT, conduite_tenir TEXT, notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE imagerie (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_imagerie TIMESTAMP NOT NULL, type_examen VARCHAR(100) NOT NULL, oeil VARCHAR(50), image_url TEXT, interpretation TEXT, notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE ordonnance (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_ordonnance TIMESTAMP NOT NULL, type_ordonnance VARCHAR(100), contenu TEXT, modele_utilise VARCHAR(255), created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE courrier (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_courrier TIMESTAMP NOT NULL, contenu TEXT, modele_utilise VARCHAR(255), created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE conclusion (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_conclusion TIMESTAMP NOT NULL, diagnostic TEXT, conduite_tenir TEXT, notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE modeleordonnance (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom_modele VARCHAR(255) NOT NULL, type VARCHAR(100) NOT NULL, contenu TEXT NOT NULL, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE modelecourrier (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom_modele VARCHAR(255) NOT NULL, objet VARCHAR(500), contenu TEXT NOT NULL, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE actemedical (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), code VARCHAR(50) NOT NULL, libelle VARCHAR(500) NOT NULL, tarif_base DECIMAL(10,2) NOT NULL, specialite VARCHAR(100), created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE cotation (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_cotation TIMESTAMP NOT NULL, actes JSONB, total_base DECIMAL(10,2), total_depassement DECIMAL(10,2), total_global DECIMAL(10,2), created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE raccourciexamen (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, categorie VARCHAR(100) NOT NULL, texte TEXT NOT NULL, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE associationcotation (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, actes_ids TEXT[], depassements DECIMAL(10,2)[], created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE traitement (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom_medicament VARCHAR(255) NOT NULL, posologie TEXT NOT NULL, duree VARCHAR(100), indication TEXT, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE lentillecontact (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), marque VARCHAR(255) NOT NULL, fabricant VARCHAR(255), type_renouvellement VARCHAR(100) NOT NULL, rayon VARCHAR(50), diametre VARCHAR(50), texte_impression TEXT, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE verresprescrit (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_prescription TIMESTAMP NOT NULL, type_vision VARCHAR(100) NOT NULL, type_verres VARCHAR(255), type_teinte VARCHAR(255), od_sphere VARCHAR(50), od_cylindre VARCHAR(50), od_axe VARCHAR(50), od_addition VARCHAR(50), og_sphere VARCHAR(50), og_cylindre VARCHAR(50), og_axe VARCHAR(50), og_addition VARCHAR(50), notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE prescriptionlentille (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_prescription TIMESTAMP NOT NULL, od_lentille_id UUID, od_sphere VARCHAR(50), od_cylindre VARCHAR(50), od_axe VARCHAR(50), od_addition VARCHAR(50), od_puissance VARCHAR(50), od_rayon VARCHAR(50), od_diametre VARCHAR(50), og_lentille_id UUID, og_sphere VARCHAR(50), og_cylindre VARCHAR(50), og_axe VARCHAR(50), og_addition VARCHAR(50), og_puissance VARCHAR(50), og_rayon VARCHAR(50), og_diametre VARCHAR(50), notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE megaraccourci (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, lampe_fente_od TEXT, lampe_fente_og TEXT, fond_oeil_od TEXT, fond_oeil_og TEXT, interpretation_imagerie TEXT, diagnostic TEXT, conduite_tenir TEXT, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE dossieratraiter (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, motif VARCHAR(255) NOT NULL, note TEXT, priorite VARCHAR(50) DEFAULT 'normale', statut VARCHAR(50) DEFAULT 'en_attente', assigne_a VARCHAR(255), created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE ivt (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_injection DATE NOT NULL, oeil VARCHAR(10) NOT NULL, acuite_visuelle VARCHAR(50), epaisseur_maculaire DECIMAL(10,2), produit_injecte VARCHAR(100), notes TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE typeverres (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, description TEXT, notes_impression TEXT, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE consultation (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), patient_id UUID REFERENCES patient(id) ON DELETE CASCADE, date_consultation TIMESTAMP NOT NULL, statut VARCHAR(50) DEFAULT 'en_attente', motif TEXT, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
CREATE TABLE raccourcioverviewophtalmo (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), nom VARCHAR(255) NOT NULL, categorie VARCHAR(100) NOT NULL, texte TEXT NOT NULL, is_global BOOLEAN DEFAULT FALSE, created_date TIMESTAMP DEFAULT NOW(), updated_date TIMESTAMP DEFAULT NOW(), created_by VARCHAR(255));
